import React,{useContext} from 'react';
import { Grid, TextField, makeStyles, InputLabel , Typography} from '@material-ui/core';
import { AppContext } from '../context';

const useStyles = makeStyles(theme => ({
    root: {
        
        padding: `0 ${theme.spacing(10)}px ${theme.spacing(10)}px ${theme.spacing(10)}px`,
    },
    label: {
        marginBottom: theme.spacing(1),
        color: 'black',
    },
    textField: {
        margin: "0 40px 0 0",
        width: "400px",
        
    },
    inputGroup: {
        marginTop: theme.spacing(2),
        
    }
}));

export default () => {
    const classes = useStyles();
    const [state, setState ] = useContext(AppContext);

    const [debtor_name,setDebtorName] = useState('');
    const [debtor_name_register_bank,setDebtorNameRegisterBank] = useState('');
    const [debtor_address,setAddress] = useState('');
    const [debtor_cont_num,setContactNum] = useState('');
    const [cell_num,setCellNum] = useState('');

    useEffect(() => {        
        setState(state => ({ ...state, debtor_name:  debtor_name}));
        setState(state => ({...state, debtor_name_register_bank: debtor_name_register_bank}));
        setState(state => ({...state, debtor_address: debtor_address}));
        setState(state => ({...state, debtor_cont_num: debtor_cont_num}));
        setState(state => ({...state, debtor_amount: debtor_amount}));
        
      }, [name_debtor, abv_name_register_bank, address, cont_num, street_addr, city, post_code, email, refered_by ],
        
      );

    return (
        <Grid container className={classes.root} justify="center">
            <Grid item md={6} xs={10} className={classes.inputGroup}>
                <InputLabel  id="fullNameLabel" className={classes.label}>Name (Debtor)</InputLabel>
                <TextField className={classes.textField} />
            </Grid>
            <Grid item md={6}  xs={10} className={classes.inputGroup}>
                <InputLabel id="idNumberLabel" className={classes.label}>Abbreviated name as registered with bank</InputLabel>
                <TextField className={classes.textField} />
            </Grid>
            <Grid item md={6}  xs={10} className={classes.inputGroup}>
                <InputLabel id="telephoneLabel" className={classes.label}>Adress</InputLabel>
                <TextField multiline fullWidth className={classes.textField} />
            </Grid>
            <Grid item md={6}  xs={10} className={classes.inputGroup}>
                <InputLabel id="cellphoneLabel" className={classes.label}>Contact Number</InputLabel>
                <TextField className={classes.textField} />
            </Grid>
            <Grid item md={6}  xs={10} className={classes.inputGroup}>
                <InputLabel id="addressLabel" className={classes.label}>Debit Amount</InputLabel>
                <TextField />
            </Grid>
            <Grid item md={6}  xs={10} className={classes.inputGroup}>
                <Typography variant="h6">Debit Order Date: {state.payDate}</Typography>
            </Grid>
           
        </Grid>
    )
}
