import React from "react";
import { CssBaseline } from '@material-ui/core';
import { createMuiTheme, ThemeProvider } from "@material-ui/core/styles";
import './Theme.css'
import color from "@material-ui/core/colors/amber";



const theme = createMuiTheme({
  typography: {
    fontFamily: [
      'Fira Sans',
      'Roboto',
      '-apple-system',
      'BlinkMacSystemFont',
      '"Segoe UI"',
      '"Helvetica Neue"',
      'Arial',
      'sans-serif',
      '"Apple Color Emoji"',
      '"Segoe UI Emoji"',
      '"Segoe UI Symbol"',
    ].join(','),
    fontWeightRegular: '500',
  },
  palette: {
    primary: {
        main: '#124b73',
    },
    secondary: {
      main: '#dfdfdf',
    },
    text :{
      primary: "#000",
      secondary: "#fff",
    },
  },
  overrides:{
      MuiCheckbox:{
        root:{
          '&:hover':{
            background: "#FFE300",
          },
          color:'#124b73',
          background: 'rgba(0,0,0,0.2)',
          '&:checked:hover':{
            background: "#FFE300",
          },

          '&checked:hover':{
            background: "#FFE300",
          },

        },
        checked:{
          '&:hover':{
            background: "#000",
          },
          background: "#000",
        }
      }
  }
});

export default function({ children }) {
  return <ThemeProvider theme={theme}>
    <CssBaseline />
    {children}
    </ThemeProvider>;
}
